function Deg2Rad(a){return a*Math.PI/180}function PythagorasEquirectangular(a,e,n,r){a=Deg2Rad(a),n=Deg2Rad(n),e=Deg2Rad(e),r=Deg2Rad(r)
var t=6371,i=(r-e)*Math.cos((a+n)/2),d=n-a,g=Math.sqrt(i*i+d*d)*t
return g}function NearestCity(a,e,n){var r,t=99999
for(index=0;index<n.length;++index){var i=PythagorasEquirectangular(a,e,n[index][1],n[index][2])
t>i&&(r=index,t=i)}var d=n[r]
return console.log(d[0]),d[0]}
